"use strict";angular.module("sbAdminApp").controller("checkCtrl",function($scope,$position,$location,$http){$scope.get=function(){$http.post("api/checkmessage",{Sender:$scope.Sender,sms:$scope.sms}).then(function(response){$scope.alerts=[{msg:response.data.userMessage,type:"error"}],$scope.messages=response.data.data},function(response){$scope.alerts=[{msg:response.data.userMessage||"Server error! Are you connected to the internet?.",type:"error"}]})}});