"use strict";angular.module("sbAdminApp").controller("formCtrl",function($scope,$http){$scope.regexTest=function(){$http.post("api/regex/testregex",{sms:$scope.sms,regex:$scope.regex}).then(function(response){console.log(response),$scope.msg=response.data.userMessage,$scope.match=response.data.data.match,$scope.showMatch=!0},function(response){$scope.alerts=[{msg:response.data.userMessage||"Server error! Are you connected to the internet?.",type:"error"}]})},$scope.textarea="[ ]",$scope.showAddButton=!0,$scope.changeTextarea=function(argument){$scope.showAddButton=!1},$scope.addRegex=function(){var arrayObj=JSON.parse($scope.textarea);console.log(arrayObj),$http.post("api/submitregex",{data:arrayObj}).then(function(response){console.log(response),$scope.textarea=[],$scope.textarea="[ ]",$scope.alerts=[{msg:response.data.userMessage,type:"success"}]},function(response){$scope.alerts=[{msg:response.data.userMessage||"Server error! Are you connected to the internet?.",type:"error"}]})}});