"use strict";angular.module("sbAdminApp").controller("loginCtrl",function($scope,$location,$http,$cookieStore,api){$scope.login=function(){api.post("login",!1,{username:$scope.username,password:$scope.password},function(err,response){$scope.success=!0,$scope.message=response.message,response.data&&($location.path("/dashboard/home"),$cookieStore.put("c2cCookie",response.data.token))})}});