"use strict";angular.module("sbAdminApp").controller("loginCtrl",function($scope,$position,$location,$http,$cookieStore){$scope.errors=!1,$scope.user=!0,$scope.login=function(){$http.post("api/modlogin",{email:$scope.email,password:$scope.password}).then(function(response){response&&($scope.user=response.data.error,$scope.success=!0,$scope.successMessage=response.data.userMessage||"Success.",response.data.data&&($cookieStore.put("c2cCookie",response.data.data),$location.path("/dashboard/home")))},function(response){$scope.errors=!0,$scope.success=!1,$scope.errorMessage=response.data.data.userMessage||"Server error."})}});