"use strict";angular.module("sbAdminApp").controller("loginCtrl",function($scope,$location,$http,$cookieStore,api,$cookies,$window){$cookieStore.remove("c2cCookie"),console.log($cookieStore.get("c2cCookie")),$scope.login=function(){api.post("login",!1,{username:$scope.username,password:$scope.password},function(err,response){$scope.success=!0,$scope.message=response.message,response.data&&($location.path("/dashboard/users"),$cookieStore.put("c2cCookie",response.data.authToken),console.log("logged in",$cookieStore.get("c2cCookie")))})}});