"use strict";angular.module("sbAdminApp").controller("regexupdateCtrl",function($scope,$position,$location,$http){$scope.getRegex=function(){$http.get("api/getregex/"+$scope.id).then(function(response){$scope.result=!0,$scope.regex=JSON.stringify(response.data.data)},function(response){$scope.alerts=[{msg:response.data.userMessage,type:"error"}]})},$scope.updateRegex=function(){var regex=JSON.parse($scope.regex);$http.post("api/modifyregex",{obj:regex}).then(function(response){$scope.alerts=[{msg:response.data.userMessage,type:"error"}]},function(response){$scope.alerts=[{msg:response.data.userMessage||"Server error! Are you connected to the internet?.",type:"error"}]})}});